#[test]
fn test_b64() {
    let b64 = r#""#;
    use base64::{engine::general_purpose, Engine as _};

    let decoded = general_purpose::STANDARD_NO_PAD.decode(b64).unwrap();
    let str = String::from_utf8_lossy(&decoded);
    println!("{str}");
}

